const agora = new Date();
const fusoHorarioBrasilia = new Date(agora.getTime() - 3 * 60 * 60 * 1000);

const ano = fusoHorarioBrasilia.getFullYear();
const mes = String(fusoHorarioBrasilia.getMonth() + 1).padStart(2, '0');
const dia = String(fusoHorarioBrasilia.getDate()).padStart(2, '0');
const hora = String(fusoHorarioBrasilia.getHours()).padStart(2, '0');
const minutos = String(fusoHorarioBrasilia.getMinutes()).padStart(2, '0');

return `${dia}/${mes}/${ano} ${hora}:${minutos}`;
